// Form scaffold for Cost Center Allocation Percentage
// Generated from Canon schema — do not edit manually
import React, { useState } from 'react';
import type { CostCenterAllocationPercentage } from '../types/cost-center-allocation-percentage.js';

interface CostCenterAllocationPercentageFormProps {
  initialData?: Partial<CostCenterAllocationPercentage>;
  onSubmit: (data: Partial<CostCenterAllocationPercentage>) => void;
  mode: 'create' | 'edit';
}

export function CostCenterAllocationPercentageForm({ initialData = {}, onSubmit, mode }: CostCenterAllocationPercentageFormProps) {
  const [formData, setFormData] = useState<Partial<CostCenterAllocationPercentage>>(initialData);

  const handleChange = (field: string, value: unknown) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <h2 className="text-xl font-semibold">{mode === 'edit' ? 'Cost Center Allocation Percentage' : 'New Cost Center Allocation Percentage'}</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700">Cost Center (→ Cost Center)</label>
            <div className="relative">
              <input
                type="text"
                placeholder="Search Cost Center..."
                value={String(formData.cost_center ?? '')}
                onChange={e => {
                  handleChange('cost_center', e.target.value);
                  // TODO: Implement async search for Cost Center
                  // fetch(`/api/resource/Cost Center?filters=[["name","like","${e.target.value}%"]]&limit=10`)
                  //   .then(r => r.json()).then(data => setSuggestions(data.data));
                }}
                className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm" required
                autoComplete="off"
                data-doctype="Cost Center"
                data-fieldname="cost_center"
              />
              {/* Link indicator */}
              {formData.cost_center && (
                <button
                  type="button"
                  onClick={() => handleChange('cost_center', '')}
                  className="absolute right-2 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  aria-label="Clear"
                >
                  ×
                </button>
              )}
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Percentage (%)</label>
            <input
              type="number"
              step="any"
              value={formData.percentage != null ? Number(formData.percentage) : ''}
              onChange={e => handleChange('percentage', e.target.value ? parseFloat(e.target.value) : undefined)}
              className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm" required
            />
          </div>

      <div className="flex justify-end gap-2">
        <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
          {mode === 'create' ? 'Create' : 'Save'}
        </button>
      </div>
    </form>
  );
}