// Form scaffold for Landed Cost Purchase Receipt
// Generated from Canon schema — do not edit manually
import React, { useState } from 'react';
import type { LandedCostPurchaseReceipt } from '../types/landed-cost-purchase-receipt.js';

interface LandedCostPurchaseReceiptFormProps {
  initialData?: Partial<LandedCostPurchaseReceipt>;
  onSubmit: (data: Partial<LandedCostPurchaseReceipt>) => void;
  mode: 'create' | 'edit';
}

export function LandedCostPurchaseReceiptForm({ initialData = {}, onSubmit, mode }: LandedCostPurchaseReceiptFormProps) {
  const [formData, setFormData] = useState<Partial<LandedCostPurchaseReceipt>>(initialData);

  const handleChange = (field: string, value: unknown) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <h2 className="text-xl font-semibold">{mode === 'edit' ? 'Landed Cost Purchase Receipt' : 'New Landed Cost Purchase Receipt'}</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700">Receipt Document Type</label>
            <select
              value={String(formData.receipt_document_type ?? '')}
              onChange={e => handleChange('receipt_document_type', e.target.value)}
              className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm" required
            >
              <option value="">Select...</option>
              <option value="Purchase Invoice">Purchase Invoice</option>
              <option value="Purchase Receipt">Purchase Receipt</option>
              <option value="Stock Entry">Stock Entry</option>
              <option value="Subcontracting Receipt">Subcontracting Receipt</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Receipt Document</label>
            <input
              type="text"
              value={String(formData.receipt_document ?? '')}
              onChange={e => handleChange('receipt_document', e.target.value)}
              className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm" required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Supplier (→ Supplier)</label>
            <div className="relative">
              <input
                type="text"
                placeholder="Search Supplier..."
                value={String(formData.supplier ?? '')}
                onChange={e => {
                  handleChange('supplier', e.target.value);
                  // TODO: Implement async search for Supplier
                  // fetch(`/api/resource/Supplier?filters=[["name","like","${e.target.value}%"]]&limit=10`)
                  //   .then(r => r.json()).then(data => setSuggestions(data.data));
                }}
                className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                autoComplete="off"
                data-doctype="Supplier"
                data-fieldname="supplier"
              />
              {/* Link indicator */}
              {formData.supplier && (
                <button
                  type="button"
                  onClick={() => handleChange('supplier', '')}
                  className="absolute right-2 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  aria-label="Clear"
                >
                  ×
                </button>
              )}
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Posting Date</label>
            <input
              type="date"
              value={String(formData.posting_date ?? '')}
              onChange={e => handleChange('posting_date', e.target.value)}
              className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Grand Total</label>
            <input
              type="number"
              step="any"
              value={formData.grand_total != null ? Number(formData.grand_total) : ''}
              onChange={e => handleChange('grand_total', e.target.value ? parseFloat(e.target.value) : undefined)}
              className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
            />
          </div>

      <div className="flex justify-end gap-2">
        <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
          {mode === 'create' ? 'Create' : 'Save'}
        </button>
      </div>
    </form>
  );
}