// Form scaffold for Production Plan Material Request
// Generated from Canon schema — do not edit manually
import React, { useState } from 'react';
import type { ProductionPlanMaterialRequest } from '../types/production-plan-material-request.js';

interface ProductionPlanMaterialRequestFormProps {
  initialData?: Partial<ProductionPlanMaterialRequest>;
  onSubmit: (data: Partial<ProductionPlanMaterialRequest>) => void;
  mode: 'create' | 'edit';
}

export function ProductionPlanMaterialRequestForm({ initialData = {}, onSubmit, mode }: ProductionPlanMaterialRequestFormProps) {
  const [formData, setFormData] = useState<Partial<ProductionPlanMaterialRequest>>(initialData);

  const handleChange = (field: string, value: unknown) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <h2 className="text-xl font-semibold">{mode === 'edit' ? 'Production Plan Material Request' : 'New Production Plan Material Request'}</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700">Material Request (→ Material Request)</label>
            <div className="relative">
              <input
                type="text"
                placeholder="Search Material Request..."
                value={String(formData.material_request ?? '')}
                onChange={e => {
                  handleChange('material_request', e.target.value);
                  // TODO: Implement async search for Material Request
                  // fetch(`/api/resource/Material Request?filters=[["name","like","${e.target.value}%"]]&limit=10`)
                  //   .then(r => r.json()).then(data => setSuggestions(data.data));
                }}
                className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm" required
                autoComplete="off"
                data-doctype="Material Request"
                data-fieldname="material_request"
              />
              {/* Link indicator */}
              {formData.material_request && (
                <button
                  type="button"
                  onClick={() => handleChange('material_request', '')}
                  className="absolute right-2 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  aria-label="Clear"
                >
                  ×
                </button>
              )}
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Material Request Date</label>
            <input
              type="date"
              value={String(formData.material_request_date ?? '')}
              onChange={e => handleChange('material_request_date', e.target.value)}
              className="mt-1 block w-full rounded border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
            />
          </div>

      <div className="flex justify-end gap-2">
        <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
          {mode === 'create' ? 'Create' : 'Save'}
        </button>
      </div>
    </form>
  );
}